sys_script_include.script:
var where = function() {
	var args = Array.prototype.slice.call(arguments);
	var field;
	var operation;
	var value;

	if (args.length == 1) {
		// Assume encoded query string provided
		return args[0];
	}
	else if (args.length == 2) {
		// Assume an "equals" operator
		return args[0] + '=' + args[1];
	}
	else {
		return args.join('');
	}
};
